import React, {useState} from "react";
import style from './container.module.css'
import ChatHeader from "../../chatUI/header/header";
import InputBox from "../../chatUI/inputBox/input";
function Container(){
    const [messages, setMessages] = useState([]);

    // const sendMessage = (message) => {
    //   const userMessage = {
    //     type: 'sent',
    //     text: message.text,
    //     audio: message.audio,
    //   };
    //   const botResponse = {
    //     type: 'received',
    //     text: 'This is a bot response',
    //   };
    //   setMessages([...messages, userMessage, botResponse]);
    // };

    const sendMessage = async (message) => {
      const userMessage = {
        type: 'sent',
        text: message.text,
        audioUrl: null,
      };
      const botResponse = {
        type: 'received',
        text: 'This is a bot response',
        audioUrl: null,
      };
      if (message.audio) {
        const formData = new FormData();
        formData.append('audio', message.audio);
        const response = await fetch('/api/save-audio', {
          method: 'POST',
          body: formData,
        });
        const audioUrl = await response.text();
        userMessage.audioUrl = audioUrl;
      }
      setMessages([...messages, userMessage, botResponse]);
    };
    
    return(
    //     <div className={style.container}>
    //     <div className={style.box}>
    //        <ChatHeader contactName="UsBhai" />
    //        <InputBox sendMessage="Hiiii" />
          
    //     </div>
    // </div>
    <div className={style.container}>
    <div className={style.box}>
      <ChatHeader contactName="UsBhai" />
      <div className={style.chatArea}>
        {messages.map((message, index) => (
          <div
            key={index}
            className={`${style.chatBubble} ${message.type === 'sent' ? style.sent : style.received}`}
          >
            {message.text}
            {message.audio && (
              <audio controls>
                <source  src={URL.createObjectURL(message.audio)} type="audio/wav" />
              </audio>
            )}
          </div>
        ))}
      </div>
      <InputBox sendMessage={sendMessage} />
    </div>
  </div>
    )
}

export default Container;
