import React, { useState , useRef} from 'react';
import style from './input.module.css'
import send from './assets/send.png';
import mic from './assets/mic.png'
function InputBox({ sendMessage }) {
  const [inputValue, setInputValue] = useState('');
  const [messages, setMessages] = useState([]);
  const [listening, setListening] = useState(false);
  const [recorder, setRecorder] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);

  const audioRef = useRef();

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  }


  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSend();
    }
  }



  const handleSend = () => {
    if (inputValue) {
      const userMessage = {
        type: 'sent',
        text: inputValue,
      };
      const botResponse = {
        type: 'received',
        text: 'This is a bot response',
      };
      setMessages([...messages, userMessage, botResponse]);
      setInputValue('');
    }
  };

  // const handleSend = () => {
  //   if (inputValue || audioRef.current) {
  //     const userMessage = {
  //       type: 'sent',
  //       text: inputValue,
  //       audio: audioRef.current,
  //     };
  //     const botResponse = {
  //       type: 'received',
  //       text: 'This is a bot response',
  //     };
  //     setMessages([...messages, userMessage, botResponse]);
  //     setInputValue('');
  //     audioRef.current = null;
  //   }
  // };
  const handleMicDown = () => {
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then((stream) => {
        const chunks = [];
        const mediaRecorder = new MediaRecorder(stream);
        mediaRecorder.ondataavailable = (e) => chunks.push(e.data);
        mediaRecorder.onstop = () => {
          const audioBlob = new Blob(chunks, { type: 'audio/mp3' });
          const audioUrl = URL.createObjectURL(audioBlob);
          const botResponse = {
            type: 'received',
            audioUrl: audioUrl
          };
          setMessages([...messages, botResponse]);
        };
        mediaRecorder.start();
        setRecorder(mediaRecorder);
      })
      .catch((err) => {
        console.log('Unable to access microphone: ', err);
      });
  };
  

  const handleMicUp = () => {
    recorder.stop();
  };
  



  // const handleMicClick = () => {
  //   const recognition = new window.webkitSpeechRecognition();
  //   recognition.lang = 'en-US';
  //   recognition.start();
  //   recognition.onresult = (event) => {
  //     const speechToText = event.results[0][0].transcript;
  //     setInputValue(speechToText);
  //   };
  //   recognition.onerror = (event) => {
  //     console.log('Speech recognition error occurred: ', event.error);
  //   };
  // };


  return (

    <div className={style.inputBox}>
    <div className={style.messages}>
      {/* <div className={style.userMessages}>
        {messages.filter((message) => message.type === 'sent').map((message, index) => (
          <div key={index} className={style.message + ' ' + style[message.type]}>
            {message.text}
          </div>
        ))}
      </div>
      <div className={style.botMessages}>
        {messages.filter((message) => message.type === 'received').map((message, index) => (
          <div key={index} className={style.message + ' ' + style[message.type]}>
            {message.text}
          </div>
        ))}
      </div>
    </div> */}
            <div className={style.userMessages}>
          {messages
            .filter((message) => message.type === 'sent')
            .map((message, index) => (
              <div key={index} className={`${style.message} ${style.sent}`}>
                {message.text}
                {message.audio && (
                  <audio controls>
                    <source src={URL.createObjectURL(message.audio)} type="audio/wav" />
                  </audio>
                )}
              </div>
            ))}
        </div>
        <div className={style.botMessages}>
          {messages
            .filter((message) => message.type === 'received')
            .map((message, index) => (
              <div key={index} className={`${style.message} ${style.received}`}>
                {message.text}
              </div>
            ))}
        </div>
      </div>
      <div className={style.input}></div>
      <i className="fas fa-grin"></i>
      <input
        type="text"
        placeholder="Type a message"
        value={inputValue}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress}
      />
      <img src={mic} alt="Microphone"
        // onClick={handleMicClick}\
        onMouseDown={handleMicDown}
        onMouseUp={handleMicUp}
        onTouchStart={handleMicDown}
        onTouchEnd={handleMicUp}

      />
      <img src={send} alt="Send" onClick={handleSend} />

    </div>
  );
}

export default InputBox;
