import React from 'react';
import style from './header.module.css'

function ChatHeader({ contactName }) {
  
  return (
    <>

    <div className={style.chatHeader}>
      <img src="https://via.placeholder.com/50" alt="Contact Avatar" className={style.contactAvatar}/>
      <div className={style.contactInfo}>
        <h2 className={style.contactName}>{contactName}</h2>
        <span className={style.contactStatus}>Online</span>
      </div>
      <div className={style.headerIcons}>
        <i className="fas fa-search"></i>
        <i className="fas fa-paperclip"></i>
        <i className="fas fa-ellipsis-v"></i>
      </div>
    </div>
        <div className={style.inputBox}>
        <i className="fas fa-grin"></i>
        <input
          type="text"
          placeholder="Type a message"
          value={inputValue}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
        />
        <i className="fas fa-microphone"></i>
      </div>
      </>
  );
}

export default ChatHeader;
