import React, { useState } from 'react';
import style from './input.module.css'
import send from './assets/send.png';
import mic from './assets/mic.png'
function InputBox({ sendMessage }) {
  const [inputValue, setInputValue] = useState('');
  const [messages, setMessages] = useState([]);
  // const [recognition, setRecognition] = useState(null);
  // const [isListening, setIsListening] = useState(false);
  const [listening, setListening] = useState(false);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  }

  
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSend();
    }
  }
  

  const handleSend = () => {
    if (inputValue) {
      const newMessage = {
        type: 'sent',
        text: inputValue,
      };
      setMessages([...messages, newMessage]);
      setInputValue('');
      setTimeout(() => {
        const botResponse = {
          type: 'received',
          text: 'This is a bot response',
        };
        setMessages([...messages, botResponse]);
      }, 1000); // simulate bot response delay
    }
  }
  
  const handleMicClick = () => {
    if (!recognition) {
      const newRecognition = new window.webkitSpeechRecognition();
      newRecognition.continuous = true;
      newRecognition.interimResults = true;
      newRecognition.lang = 'en-US';
      newRecognition.onresult = (event) => {
        let finalTranscript = '';
        let interimTranscript = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript;
          } else {
            interimTranscript += transcript;
          }
        }
        setInputValue(finalTranscript);
      };
      setRecognition(newRecognition);
    }

    if (!isListening) {
      setIsListening(true);
      recognition.start();
    } else {
      setIsListening(false);
      recognition.stop();
    }
  }

  return (
    <div className={style.inputBox}>
       <div className={style.messages}>
        {messages.map((message, index) => (
          <div
            key={index}
            className={style.message + ' ' + style[message.type]}
          >
            {message.text}
          </div>
        ))}
      </div>
      <div className={style.input}></div>
      <i className="fas fa-grin"></i>
      <input
        type="text"
        placeholder="Type a message"
        value={inputValue}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress}
      />
      <img src={mic} alt="Microphone"
              onClick={handleMicClick}
              className={isListening ? style.active : ''}
               />
      <img src={send} alt="Send" onClick={handleSend} />

    </div>
  );
}

export default InputBox;
