import React, { useState } from 'react';
import style from './input.module.css'
import send from './assets/send.png';
import mic from './assets/mic.png'
function InputBox({ sendMessage }) {
  const [inputValue, setInputValue] = useState('');
  const [messages, setMessages] = useState([]);
  // const [recognition, setRecognition] = useState(null);
  // const [isListening, setIsListening] = useState(false);
  const [listening, setListening] = useState(false);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  }

  
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSend();
    }
  }
  

  const handleSend = () => {
    if (inputValue) {
      const newMessage = {
        type: 'sent',
        text: inputValue,
      };
      setMessages([...messages, newMessage]);
      setInputValue('');
      setTimeout(() => {
        const botResponse = {
          type: 'received',
          text: 'This is a bot response',
        };
        setMessages([...messages, botResponse]);
      }, 1000); // simulate bot response delay
    }
  }
  
  const handleMicClick = () => {
    if (!('SpeechRecognition' in window)) {
      alert('Sorry, speech recognition is not supported by your browser.');
      return;
    }
    const recognition = new window.SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;
    if (!listening) {
      setListening(true);
      recognition.start();
      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setInputValue(transcript);
        handleSend();
      };
      recognition.onend = () => {
        setListening(false);
      };
    } else {
      recognition.stop();
    }
  }

  return (
    <div className={style.inputBox}>
       <div className={style.messages}>
        {messages.map((message, index) => (
          <div
            key={index}
            className={style.message + ' ' + style[message.type]}
          >
            {message.text}
          </div>
        ))}
      </div>
      <div className={style.input}></div>
      <i className="fas fa-grin"></i>
      <input
        type="text"
        placeholder="Type a message"
        value={inputValue}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress}
      />
      <img src={mic} alt="Microphone"
              onClick={handleMicClick}
      
               />
      <img src={send} alt="Send" onClick={handleSend} />

    </div>
  );
}

export default InputBox;
